INSERT INTO `products` (`id`, `created_at`, `updated_at`, `ten`, `alias`, `masp`, `congdung`, `cachdung`, `gia`, `anhdaidien`, `thumb`, `donggoi`, `category_id`) VALUES
(2, '2016-02-23 20:23:13', '2016-03-23 09:28:37', 'Ketosteril-Thuốc cho người suy thận ', 'ketosteril-thuoc-cho-nguoi-suy-than', '107220 ', '                     <p><span style="color: rgb(0, 128, 128);"><span style="font-size: large;">Ketosteril</span></span></p>  <p><br />  <strong>Th&agrave;nh phần: </strong><br />  Calcium-DL-3-methyl-2-oxo-valerate (&alpha;-keto tương tự Isoleucine, muối calcium)<br />  Calcium-4-methyl-2-oxo-valerate (&alpha;-keto tương tự Leucine, muối calcium)<br />  Calcium-2-oxo-3-phenyl-propionate (&alpha;-keto tương tự Phenylalanine, muối calcium)<br />  Calcium-3-methyl-2-oxo-butyrate (&alpha;-keto tương tự Valine, muối calcium)<br />  Calcium-DL-2-hydroxy-4 (methylthio)-butyrate (&alpha;-keto tương tự Methionine, muối calcium)<br />  L-Lysine acetate<br />  L-Threonine<br />  L-Tryptophan<br />  L-Histidine<br />  L-Tyrosine</p>                ', '             	<p>&nbsp;</p>  <h1 class="fon1" style="text-align: center;"><span style="color: rgb(0, 128, 128);">Ketosteril		</span></h1>  <p>&nbsp;</p>  <p>&nbsp;</p>  <p><span style="color: rgb(0, 128, 128);"><u><em><strong><span style="font-size: small;">Ph&acirc;n nh&oacute;m:</span></strong></em></u></span> THUỐC D&Ugrave;NG CHO BỆNH NH&Acirc;N SUY THẬN</p>  <p><span style="font-size: small;"><span style="color: rgb(0, 128, 128);"><u><em><strong>Th&agrave;nh Phần:</strong></em></u></span></span></p>  <p style="margin-left: 40px;">Mỗi vi&ecirc;n Ca-3-methyl-2-oxovalerate 67mg, Ca-4-methyl-2-oxovalerate 101mg,</p>  <p style="margin-left: 40px;">Ca-2-methyl-3-phenylpropionate 68mg, Ca-3-methyl-2-oxobutyrate 86mg,</p>  <p style="margin-left: 40px;">Ca-DL-methyl-2-hydroxy-4</p>  <p style="margin-left: 40px;">(methylthio-butyrate) 59mg, L-lysine-acetate 105mg,</p>  <p style="margin-left: 40px;">L-threonine 53mg, L-tryptophan 23mg, L-histidine 38mg, L-tyrosine 30mg, N 36mg, Ca 50 mg.</p>  <p><span style="font-size: small;"><u><em><strong><span style="color: rgb(0, 128, 128);">Chỉ Định:</span></strong></em></u></span></p>  <p style="margin-left: 40px;">Dự ph&ograve;ng v&agrave; điều trị những tổn hại do hư hỏng hay thiếu hụt trong chuyển  h&oacute;a protein ở suy thận m&atilde;n t&iacute;nh bằng c&aacute;ch kết hợp với chế độ ăn hạn chế  protein &lt;= 40 g/ng&agrave;y (ở người lớn) hay thấp hơn, ở bệnh nh&acirc;n c&oacute; độ  lọc cầu thận (GFR) trong khoảng 5-40 mL/ph&uacute;t.</p>  <p><u><em><strong><span style="color: rgb(0, 128, 128);"><span style="font-size: small;">Liều D&ugrave;ng: </span></span></strong></em></u></p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Người lớn: uống 3 - 4 vi&ecirc;n/lần x 3 lần/ng&agrave;y trong c&aacute;c bữa ăn (1 vi&ecirc;n/5 kg thể trọng/ng&agrave;y).<br />  <br />  <u><em><strong><span style="color: rgb(0, 128, 128);"><span style="font-size: small;">Chống Chỉ Định:</span></span></strong></em></u></p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tăng Ca m&aacute;u, rối loạn chuyển h&oacute;a acid amin.<br />  <br />  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thận Trọng: Kiểm tra thường kỳ Ca trong huyết thanh. Bảo đảm cung cấp đủ calo cho người bệnh.<br />  <span style="color: #0000ff;"><br />  </span><span style="color: rgb(0, 128, 128);"><span style="font-size: small;"><u><em><strong>Phản Ứng C&oacute; Hại:</strong></em></u></span></span> Tăng Ca m&aacute;u.<br />  <br />  <u><em><strong><span style="color: rgb(0, 128, 128);"><span style="font-size: small;">Tương  T&aacute;c Thuốc:</span></span></strong></em></u> Thuốc c&oacute; Ca l&agrave;m tăng Ca m&aacute;u. Giảm d&ugrave;ng Al(OH)3 v&igrave; ketosteril  cải thiện triệu chứng tăng ur&ecirc;- m&aacute;u. Tr&aacute;nh d&ugrave;ng với thuốc l&agrave;m giảm độ  h&ograve;a tan Ca (như Tetracyclin).<br />  <span style="color: #0000ff;"><br />  </span><span style="color: rgb(0, 128, 128);"><span style="font-size: small;"><u><em><strong>Tr&igrave;nh B&agrave;y &amp; Gi&aacute;:  </strong></em></u></span></span><br />  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Dạng Đ&oacute;ng G&oacute;i :Vi&ecirc;n n&eacute;n bao film &nbsp;&nbsp; &nbsp;600 mg x 100 vi&ecirc;n</p>            ', '1410000', 'public/crawler/ketosteril-thuoc-cho-nguoi-suy-thande356bd501f78f6fb6d55c4d08272410.png', '', ' ', 1),
(3, '2016-02-23 20:23:13', '2016-02-23 20:23:13', 'Everles', 'everles', '111950 ', '                     <p><span style="color: rgb(0, 128, 128);"><span style="font-size: larger;"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><span style="font-size: medium;"><strong> Everles </strong></span></span></span></p>  <p><span style="font-size:14px;"><span style="font-family:times new roman,times,serif;"><strong>Số ĐK:</strong>&nbsp;6210/2013/ATTP- XNCB<br />  <strong>Dạng b&agrave;o chế:</strong>&nbsp;Vi&ecirc;n nang mềm<br />  <strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i: &nbsp;</strong>hộp 15 vi&ecirc;n.<br />  <strong>C&ocirc;ng ty đăng k&yacute;:</strong>&nbsp;TOPCO</span></span><span style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"> </span></p>                ', '             	<p><span style="color: rgb(0, 128, 128);">&nbsp;<strong><em style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><u>Th&agrave;nh phần cấu tạo</u></em><span style="font-family: Arial,Helvetica,sans-serif; line-height: 22px;">:</span></strong><span style="font-family: Arial,Helvetica,sans-serif; line-height: 22px;"> </span></span><span style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">Soy isoflavone 50mg, Ginko biloba extract (leaf) 60mg, Flaxeed ( linseed) oil 210mg. Th&agrave;nh phần kh&aacute;c: vừa đủ 1 vi&ecirc;n.</span></p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><span style="color: rgb(0, 128, 128);"><strong><i><u>C&ocirc;ng dụng:</u></i></strong></span></p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">-  Bổ sung nguồn estrogen thực vật gi&uacute;p c&acirc;n bằng nội tiết tố nữ, tăng tiết  dịch nhờn ( giảm kh&ocirc; &acirc;m đạo), điều h&ograve;a kinh nguyệt, tăng cường sinh l&yacute;  nữ, cải thiện c&aacute;c triệu chứng tiền m&atilde;n kinh, m&atilde;n kinh, gi&uacute;p cơ thể tăng  nội tiết tố nữ một c&aacute;ch tự nhi&ecirc;n, gi&uacute;p nữ giới ph&aacute;t triển đầy đặn hơn.  Hạn chế l&atilde;o h&oacute;a, giảm nếp nhăn tr&ecirc;n da, mờ vết n&aacute;m, gi&uacute;p da đẹp mịn  m&agrave;ng, sắc mặt hồng h&agrave;o.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">- Tăng cường tr&iacute; nhớ giảm căng thẳng, giảm stress, chống mất ngủ.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><span style="color: rgb(0, 128, 128);"><strong><em><u>Đối tượng sử dụng v&agrave; c&aacute;ch d&ugrave;ng:</u></em></strong></span><strong>&nbsp;</strong>&nbsp;d&ugrave;ng trong c&aacute;c trường hợp sau:</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">Nữ  giới chức năng sinh l&yacute; giảm s&uacute;t, kh&ocirc; &acirc;m đạo, do suy giảm hormone sinh  dục, nữ giới tuổi tiền m&atilde;n kinh c&oacute; c&aacute;c triệu chứng: Da nhăn, n&aacute;m, ngực  chảy sệ, k&eacute;m săn chắc, t&oacute;c kh&ocirc;, dễ rụng, bốc hỏa, mất ngủ, t&iacute;ch mỡ bụng,  lo&atilde;ng xương.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><span style="color: rgb(0, 128, 128);"><strong><em><u>Một số biểu hiện nhận thấy sau khi sử dụng</u></em>:</strong></span></p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">10 ng&agrave;y: Chống mất ngủ, giảm căng thẳng.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">15 ng&agrave;y: Dịch tiết &acirc;m đạo tăng, chức năng sinh l&yacute; cải thiện.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">30 ng&agrave;y: Da mặt s&aacute;ng v&agrave; mịn dần l&ecirc;n.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">60 ng&agrave;y: Cơ thể khỏe mạnh, da hồng h&agrave;o, giảm th&acirc;m n&aacute;m, t&oacute;c khỏe v&agrave; mượt , kinh nguyệt đều.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">90 ng&agrave;y: Ngực giảm chảy xệ, căng v&agrave; đầy đặn hơn.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><span style="color: rgb(0, 128, 128);"><strong><em><u>C&aacute;ch d&ugrave;ng</u></em>:&nbsp;</strong></span>Ng&agrave;y uống 1 đến 2 vi&ecirc;n.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><span style="color: rgb(0, 128, 128);"><strong><em><u>Ch&uacute; &yacute;:</u></em></strong></span><strong><em>&nbsp;</em></strong><em>Sản phẩm n&agrave;y kh&ocirc;ng phải l&agrave; thuốc kh&ocirc;ng c&oacute; t&aacute;c dụng thay thế thuốc chữa bệnh.</em></p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><span style="color: rgb(0, 128, 128);"><strong><em><u>Hướng dẫn bảo quản</u></em>:</strong></span><strong>&nbsp;</strong>Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh s&aacute;ng.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><span style="color: rgb(0, 128, 128);"><strong><em><u>Hạn sử dụng</u>:</em></strong></span><strong>&nbsp;</strong>Xem tr&ecirc;n vỏ hộp.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><span style="color: rgb(0, 128, 128);"><strong><u>Khối lượng tịch v&agrave; quy c&aacute;ch đ&oacute;ng g&oacute;i</u>:</strong></span><strong>&nbsp;&nbsp;</strong>vỉ 15 vi&ecirc;n, hộp 1 vỉ.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;"><span style="color: rgb(0, 128, 128);"><u><strong>Sản xuất bởi:</strong></u></span><strong>&nbsp;</strong>C&ocirc;ng ty GMP Pharmaceuticals, địachỉ: 7 to9 Amax Avenue, Girraween, New South Wales 2145, Australia.</p>  <p style="font-family: Arial, Helvetica, sans-serif; line-height: 22px;">&nbsp;</p>            ', '0', 'public/crawler/everles2204b8fa01b3ae792abb5ca65f5392dd.png', '', 'hop 15 vien ', 1),
(4, '2016-02-23 20:23:13', '2016-02-23 20:23:13', 'Soy ogenba ', 'soy-ogenba', '111976 ', '                     <p>&nbsp;</p>  <h4 class="title-product">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a><span style="color: rgb(0, 128, 128);"><span style="font-size: medium;">Soy ogenba</span></span></a></h4>  <p style="margin:0in;margin-bottom:.0001pt;line-height:120%"><span style="font-size:14px;"><span style="font-family:times new roman,times,serif;"><strong><span style="line-height: 120%;">Số ĐK:</span></strong><span style="line-height: 120%;">&nbsp;6211/2013/ATTP- XNCB.<br />  <strong>Dạng b&agrave;o chế:</strong>&nbsp;Vi&ecirc;n nang mềm<br />  <strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong>&nbsp;hộp 15 vi&ecirc;n<br />  <strong>C&ocirc;ng ty đăng k&yacute;:</strong>&nbsp;TOPCO</span></span></span></p>  <p><span style="font-size:14px;"><span style="font-family:times new roman,times,serif;"><strong><span style="line-height: 120%;">Made in Australia</span></strong></span></span></p>                ', '             	<p><span style="color: rgb(0, 128, 128);">&nbsp;<strong style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><em><u>Th&agrave;nh phần cấu tạo</u></em></strong><strong style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">:</strong></span><strong style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">&nbsp;</strong><span style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">Soy isoflavone 50mg, Ginko biloba extract (leaf) 60mg, Flaxeed ( linseed) oil 210mg. Th&agrave;nh phần kh&aacute;c: vừa đủ 1 vi&ecirc;n.</span></p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><span style="color: rgb(0, 128, 128);"><b><i><u>C&ocirc;ng dụng:</u></i></b></span></p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><strong>-</strong>&nbsp;Bổ  sung nguồn estrogen thực vật gi&uacute;p c&acirc;n bằng nội tiết tố nữ, tăng tiết  dịch nhờn ( giảm kh&ocirc; &acirc;m đạo), điều h&ograve;a kinh nguyệt, tăng cường sinh l&yacute;  nữ, cải thiện c&aacute;c triệu chứng tiền m&atilde;n kinh, m&atilde;n kinh, gi&uacute;p cơ thể tăng  nội tiết tố nữ một c&aacute;ch tự nhi&ecirc;n, gi&uacute;p nữ giới ph&aacute;t triển đầy đặn hơn.  Hạn chế l&atilde;o h&oacute;a, giảm nếp nhăn tr&ecirc;n da, mờ vết n&aacute;m, gi&uacute;p da đẹp mịn  m&agrave;ng, sắc mặt hồng h&agrave;o.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">- Tăng cường tr&iacute; nhớ giảm căng thẳng, giảm stress, chống mất ngủ.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><span style="color: rgb(0, 128, 128);"><strong><em><u>Đối tượng sử dụng v&agrave; c&aacute;ch d&ugrave;ng:</u></em></strong></span><strong>&nbsp;&nbsp;</strong>d&ugrave;ng trong c&aacute;c trường hợp sau:</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">Nữ  giới chức năng sinh l&yacute; giảm s&uacute;t, kh&ocirc; &acirc;m đạo, do suy giảm hormone sinh  dục, nữ giới tuổi tiền m&atilde;n kinh c&oacute; c&aacute;c triệu chứng: Da nhăn, n&aacute;m, ngực  chảy sệ, k&eacute;m săn chắc, t&oacute;c kh&ocirc;, dễ rụng, bốc hỏa, mất ngủ, t&iacute;ch mỡ bụng,  lo&atilde;ng xương.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><span style="color: rgb(0, 128, 128);"><strong><em><u>Một số biểu hiện nhận thấy sau khi sử dụng</u></em></strong><strong>:</strong></span></p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">10 ng&agrave;y: Chống mất ngủ, giảm căng thẳng.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">15 ng&agrave;y: Dịch tiết &acirc;m đạo tăng, chức năng sinh l&yacute; cải thiện.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">30 ng&agrave;y: Da mặt s&aacute;ng v&agrave; mịn dần l&ecirc;n.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">60 ng&agrave;y: Cơ thể khỏe mạnh, da hồng h&agrave;o, giảm th&acirc;m n&aacute;m, t&oacute;c khỏe v&agrave; mượt , kinh nguyệt đều.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif">90 ng&agrave;y: Ngực giảm chảy xệ, căng v&agrave; đầy đặn hơn.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><span style="color: rgb(0, 128, 128);"><strong><em><u>C&aacute;ch d&ugrave;ng</u></em></strong><strong>:</strong></span><strong>&nbsp;</strong>Ng&agrave;y uống 1 đến 2 vi&ecirc;n.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><span style="color: rgb(0, 128, 128);"><strong><em><u>Ch&uacute; &yacute;:</u></em></strong></span><strong><em>&nbsp;</em></strong><em>Sản phẩm n&agrave;y kh&ocirc;ng phải l&agrave; thuốc kh&ocirc;ng c&oacute; t&aacute;c dụng thay thế thuốc chữa bệnh.</em></p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><span style="color: rgb(0, 128, 128);"><strong><em><u>Hướng dẫn bảo quản</u></em></strong><strong>:</strong></span><strong>&nbsp;</strong>Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh s&aacute;ng.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><span style="color: rgb(0, 128, 128);"><strong><em><u>Hạn sử dụng</u></em></strong><strong><em>:</em></strong></span><strong>&nbsp;</strong>Xem tr&ecirc;n vỏ hộp.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><span style="color: rgb(0, 128, 128);"><strong><u>Khối lượng tịch v&agrave; quy c&aacute;ch đ&oacute;ng g&oacute;i</u></strong><strong>:</strong></span><strong>&nbsp;&nbsp;</strong>vỉ 15 vi&ecirc;n, hộp 1 vỉ.</p>  <p style="line-height: 22px; font-family: Arial, Helvetica, sans-serif"><span style="color: rgb(0, 128, 128);"><strong>Sản xuất bởi:</strong></span><strong> &nbsp;</strong>GMP Pharmaceuticals, địachỉ: 7 to9 Amax Avenue, Girraween, New South Wales 2145, Australia.</p>            ', '0', 'public/crawler/soy-ogenba0352975dd59e755c928b1032e2b1ebf7.png', '', '15 viên/hộp ', 1),
